mapper.select:
       SELECT 
		
        	 FROM SZ_JOIN_MERCHANT T1
			 WHERE  T1.USER_ID = #{userid,jdbcType=BIGINT}
			   AND  t1.state = #{state,jdbcType=BIGINT}
